const vorpal=new require("vorpal")();const _commands=require("./src/matcher-commands.js");const envVars=require("./.env_vars.json");vorpal.delimiter("matcher-cli > ").show();if(envVars.ENV==="PROD"){_commands=envVars.COMMAND_ENDPOINT}const{commands:commands}=_commands;vorpal.command("matcher <query>",`\n          !USAGE COMMANDS!\n\n          update: Updates matcher environment.\n          clear: Clears console, and exits.\n          vsnap: Takes headless screenshot of the browser live-env.\n          matches: Outputs pairs of matched key-points.\n          corners: Outputs all eligible match points.\n         `).action(vorpalify);if(process.argv[2]==="vsnap"){vorpalify({query:"vsnap"})}async function vorpalify(args){const{query:query}=args;summoner=await commands.summoner;if(eval(`commands.${query}`)!==undefined){eval(`commands.${query}()`)}else if(eval(`summoner.${query}`)){console.log(eval(`summoner.${query}`))}else{error(`Invalid command: "${query}", exiting...\n`);process.exit()}}